const db = require('db');

function disable_all(cb) {

  cb = cb || function() {};

  db.games_schemas.update({}, {
    $set: {
      disable: true
    }
  }, {
    multi: true
  }, function(err, result) {

    if (err) {
      return cb({
        status: 500,
        err: err
      })
    }

    return cb(null, result);
  })
}

module.exports = disable_all;
