const db = require('db');

function get_map(game_uuid, cb) {

  cb = cb || function() {};

  if (!game_uuid) {
    return cb({
      status: 400,
      err: new Error('low args')
    })
  }

  let query = {
    uuid: game_uuid
  };

  db.games.findOne(query, {
    map: true
  }).lean().exec(function(err, game) {

    if (err) {
      return cb({
        status: 500,
        err: err
      });
    }

    if (!game || !game.map) {
      return cb({
        status: 404,
        err: new Error('game or map not found.')
      });
    }

    cb(null, game.map);
  })
}

module.exports = get_map;
