const db = require('db');

function list(view, cb) {

  cb = cb || function() {};
  view = view || {};

  let query = {
    $or: [{
      disable: false
    }, {
      disable: {
        $exists: false
      }
    }]
  }

  db.games_schemas.find(query, view).lean().exec(function(err, game_schemas) {
    if (err) {
      return cb({
        status: 500,
        err: err
      });
    }

    return cb(null, game_schemas);
  })
}

module.exports = list;
