const mongoose = require('mongoose');
const Schema = mongoose.Schema;
const ObjectId = Schema.Types.ObjectId;
mongoose.Promise = global.Promise;

let games_schema = Schema({

  game_schema_id: {
    type: ObjectId,
    ref: 'game_schemas'
  },

  // uuids of users
  fighters: [String],

  // uuid of the game
  uuid: String,

  map: [],
  data: {},
  logs: [{}],

  starts: Boolean,
  finished: Boolean
});


module.exports = mongoose.model('games', games_schema);
