const mongoose = require('mongoose');
const Schema = mongoose.Schema;
const ObjectId = Schema.Types.ObjectId;
mongoose.Promise = global.Promise;

let games_schema = Schema({

  game_schema_id: {
    type: ObjectId,
    ref: 'game_schemas'
  },

  // uuids of users
  users_uuid: [String],

  // uuid of the game
  uuid: String,

  // -1 is first_user and 1 is the second user
  map: [0, 0, 0, 0, 0, 0, 0, 0, 0],

  // logs of the game
  logs: [{
    uuid: String,

    // number from 1 to 9, this is palying game
    choose: Number
  }],

  turn_uuid: String,

  finished: Boolean
});


module.exports = mongoose.model('games', games_schema);
