const mongoose = require('mongoose');
const Schema = mongoose.Schema;
const ObjectId = Schema.Types.ObjectId;
mongoose.Promise = global.Promise;

let games_schemas_schema = Schema({

  name: String,
  desc: String,
  auther: String,
  played: Number,

  // absolute path of the game [index.js]
  path: String,
  disable: Boolean
});


module.exports = mongoose.model('games_schemas', games_schemas_schema);
